import React, { useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import 'bootstrap/dist/css/bootstrap.min.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [query, setQuery] = useState("");
  const [summary, setSummary] = useState("");
  const [tableData, setTableData] = useState([]);
  const [chartData, setChartData] = useState({});

  const handleAnalyze = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/realestate/");
      const data = Array.isArray(response.data.realestate) ? response.data.realestate : [];

      // Filter by "final location"
      const filtered = data.filter((item) => {
        const location = (item["final location"] || "").trim().toLowerCase();
        const queryNormalized = query.trim().toLowerCase();
        return location.includes(queryNormalized);
      });

      setTableData(filtered);

      // Calculate average price
      const avgPrice =
        filtered.reduce(
          (sum, item) => sum + Number(item["flat - weighted average rate"] || 0),
          0
        ) / (filtered.length || 1);

      setSummary(
        `Found ${filtered.length} entries for "${query}". Average price: $${avgPrice.toFixed(2)}`
      );

      // Chart data
      const years = filtered.map((i) => i.year || "Unknown");
      const prices = filtered.map((i) => Number(i["flat - weighted average rate"] || 0));

      setChartData({
        labels: years,
        datasets: [
          {
            label: "Price Trend",
            data: prices,
            borderColor: "blue",
            backgroundColor: "lightblue",
          },
        ],
      });
    } catch (error) {
      console.error(error);
      setSummary("Error fetching data from backend.");
      setTableData([]);
      setChartData({});
    }
  };

  return (
    <div className="container mt-5">
      <h2>Real Estate Analysis Chatbot</h2>

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Enter area name"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <button className="btn btn-primary mb-3" onClick={handleAnalyze}>
        Analyze
      </button>

      {summary && <div className="alert alert-info">{summary}</div>}

      {chartData.labels && chartData.labels.length > 0 && (
        <Line data={chartData} />
      )}

      {tableData.length > 0 && (
        <table className="table table-striped mt-3">
          <thead>
            <tr>
              <th>Year</th>
              <th>Area</th>
              <th>Price</th>
              <th>Demand</th>
              <th>Size (sqft)</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((item, index) => (
              <tr key={index}>
                <td>{item.year}</td>
                <td>{item["final location"]}</td>
                <td>{item["flat - weighted average rate"]}</td>
                <td>{item["total sold - igr"]}</td>
                <td>{item["total carpet area supplied (sqft)"]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
