import React, { useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [query, setQuery] = useState("");
  const [summary, setSummary] = useState("");
  const [tableData, setTableData] = useState([]);
  const [chartData, setChartData] = useState({});

  const handleAnalyze = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/realestate/");
      const data = response.data.realestate;

      // Filter by query (area)
     const filtered = data.filter((item) => {
  // Skip if area is missing or null
  if (!item.area || typeof item.area !== "string") return false;

  // Skip if query is empty, return all valid items
  if (query.trim() === "") return true;

  return item.area.toLowerCase().includes(query.toLowerCase());
});


      // Mock summary
      setSummary(
        `Found ${filtered.length} entries for "${query}". Average price: ${
          filtered.reduce((sum, item) => sum + item.price, 0) /
          (filtered.length || 1)
        }`
      );

      // Prepare chart data (price vs year)
      const years = filtered.map((item) => item.year);
      const prices = filtered.map((item) => item.price);

      setChartData({
        labels: years,
        datasets: [
          {
            label: "Price Trend",
            data: prices,
            borderColor: "blue",
            backgroundColor: "lightblue",
          },
        ],
      });
    } catch (error) {
      console.error(error);
      setSummary("Error fetching data from backend.");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Real Estate Analysis Chatbot</h2>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter area name"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mb-3" onClick={handleAnalyze}>
        Analyze
      </button>

      {summary && <div className="alert alert-info">{summary}</div>}

      {chartData.labels && (
        <div className="mb-3">
          <Line data={chartData} />
        </div>
      )}

      {tableData.length > 0 && (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Year</th>
              <th>Area</th>
              <th>Price</th>
              <th>Demand</th>
              <th>Size</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((item, index) => (
              <tr key={index}>
                <td>{item.year}</td>
                <td>{item.area}</td>
                <td>{item.price}</td>
                <td>{item.demand}</td>
                <td>{item.size}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
